import styled from "styled-components";

export const Title = styled.h2`
  font-size: 1.5em;
  color: #000055;
`;

export const Input = styled.input`
  border: 1px solid #333;
  height: 30px;
  width: 500px;
`;

export const Error = styled.div`
  margin-top: 1em;
  margin-bottom: 1em;
  text-align: center;
  font-size: 1.5em;
  color: #ff1111;
`;

export const OK = styled.div`
  margin-top: 1em;
  margin-bottom: 1em;
  text-align: center;
  font-size: 1.5em;
  color: #11aa11;
`;
