import React, {useEffect} from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import { OK, Error, Title } from "../style/styles";
import { useRecoilState } from "recoil";
import { recipeState } from "../recoil/atoms";

const RECIPE_DETAILS_QUERY = gql`
    query recipe($id: ID!) {
        recipe( id: $id ) {
            title
            description
            steps {
                description
                image
            }
            date
            ingredients {
                name
            }
            mainImage

        }
    }
`;

export default () => {
    const [recipe] = useRecoilState(recipeState);

    const { loading, error, data } = useQuery(RECIPE_DETAILS_QUERY, {
        variables: { id: recipe },
        notifyOnNetworkStatusChange: true,
    });
    if (loading) return <p>Cargando receta...</p>; 

    let content;
    if (data) {
        content =
            <Recipe>
                <Details></Details>
                <Ingredients></Ingredients>
            </Recipe>
    }

    return (
        {content}
    );
};

const Recipe = styled.div`
  margin-left: 1em;
`;
const Details = styled.div`
  margin-left: 1em;
`;
const Ingredients = styled.div`
  margin-left: 1em;
`;
