import React from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import { OK, Error, Title } from "../style/styles";
import { useRecoilState } from "recoil";
import { recipeState } from "../recoil/atoms";

const RECIPE_DETAILS_QUERY = gql`
  {
    recipe {
      title
    }
  }
`;

export default () => {
    const [recipe] = useRecoilState(recipeState);

    let content;

    if (recipe) {
        const { loading, error, data } = useQuery(RECIPE_DETAILS_QUERY);
        if (loading) return <p>Cargando receta...</p>;
        if (error) return <p>Error cargando la receta...</p>;

        content = <Title>{data.recipe.title}</Title>
    }


    return (
        {content}
    );
};

const RecipesList = styled.div`
  color: #333333;
  margin: 2em;
  display: flex;
  flex-direction: column;
`;

const Recipe = styled.div`
  margin-left: 1em;
`;
