import React, {useEffect} from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import styled from "styled-components";
import { OK, Error, Title } from "../style/styles";
import { useRecoilState } from "recoil";
import { recipeState } from "../recoil/atoms";

const RECIPE_DETAILS_QUERY = gql`
    query recipe($id: ID!) {
        recipe( id: $id ) {
            title
            description
            steps {
                description
                image {
                    url
                    mimetype
                }
            }
            date
            ingredients {
                name
            }
            mainImage {
                url
                mimetype
            }
        }
    }
`;

export default () => {
    const [recipe] = useRecoilState(recipeState);

    const { loading, data } = useQuery(RECIPE_DETAILS_QUERY, {
        variables: { id: recipe },
        notifyOnNetworkStatusChange: true,
    });
    if (loading) return <p>Cargando receta...</p>; 

    let content;
    if (data) {
        content =
            <Recipe>
                <Details>
                    <Title>{data.recipe.date}</Title>
                    <Date>{data.recipe.description}</Date>
                    <img className="Img" src={"http://77.228.91.193/" + data.recipe.mainImage.url} alt={data.recipe.mainImage.mimetype}/>
                    <Description>{data.recipe.description}</Description>
                </Details>
                <Ingredients></Ingredients>
            </Recipe>
    }

    return (
        <div>{content}</div>
    );
};

const Recipe = styled.div`
  margin-left: 1em;
`;
const Details = styled.div`
  margin-left: 1em;
`;
const Ingredients = styled.div`
  margin-left: 1em;
`;
const Description = styled.div`
  margin-left: 1em;
`;
const Date = styled.div`
  margin-left: 1em;
`;
